  `submission_ts` int DEFAULT NULL,
  `submission_exchange_rate` decimal(10,2) DEFAULT NULL,
  `confirmed_ts` int DEFAULT NULL,
  `decision_deposit_address` varchar(128) DEFAULT NULL,
  `decision_deposit_amount` bigint DEFAULT NULL,
  `deciding_since` int DEFAULT NULL,
  `executed_ts` int DEFAULT NULL,
  `executed_exchange_rate` decimal(10,2) DEFAULT NULL,
  `confirming_since` int DEFAULT NULL,
  `tally_ayes` bigint DEFAULT NULL,
  `tally_nays` bigint DEFAULT NULL,
  `tally_support` bigint DEFAULT NULL,
  `title` varchar(255) DEFAULT NULL,
  `description` text,
  PRIMARY KEY (`id`),
  KEY `network_id` (`network_id`),
  CONSTRAINT `referenda_ibfk_1` FOREIGN KEY (`network_id`) REFERENCES `networks` (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=963 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_0900_ai_ci;
